    private void btn_InserirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_InserirActionPerformed
        Funcionario f1 = new Funcionario();
        
        f1.nome = txt_nome.getText();
        f1.cargo = txt_cargo.getText();
        f1.idade = Integer.parseInt(txt_idade.getText());
        f1.salario = Double.parseDouble(txt_salario.getText());
        
        JOptionPane.showMessageDialog(null,"NOME: " + f1.nome + "\n CARGO: " +
                f1.cargo + "\n IDADE: " + f1.idade + "\n SALARIO: R$" + f1.salario);
    }//GEN-LAST:event_btn_InserirActionPerformed

